record.field:
for record in records:
    for line in record.order_line:
        if line.product_template_id.x_split_products and line.product_template_id.product_line_kit_ids:
            for product_line in line.product_template_id.product_line_kit_ids:
                product_kit_vals = {
                    'product_id': product_line.product_kit_template_id.id,
                    'product_uom_qty': product_line.quantity_per_product,
                    'order_id': record.id,
                }
                env['sale.order.line'].create(product_kit_vals)

            # Remove the original line after creating the product_kit_template_id lines
            line.unlink()

for record in records:
    vkoo_products_item_inherit = env.ref('poweron_product_kits_addons.vkoo_products_item_inherit')
    vkoo_product_price_inherit = env.ref('poweron_product_kits_addons.vkoo_product_price_inherit')
    vkoo_cart_lines_inherit = env.ref('poweron_product_kits_addons.vkoo_cart_lines_inherit')
    vkoo_total_inherit = env.ref('poweron_product_kits_addons.vkoo_total_inherit')
    vkoo_cart_summary_inherit = env.ref('poweron_product_kits_addons.vkoo_cart_summary_inherit')

    if not record.x_anfrage_modus_mode:
        vkoo_products_item_inherit.write({
            'active': False
        })
        vkoo_product_price_inherit.write({
            'active': False
        })
        vkoo_cart_lines_inherit.write({
            'active': False
        })
        vkoo_total_inherit.write({
            'active': False
        })
        vkoo_cart_summary_inherit.write({
            'active': False
        })
    elif record.x_anfrage_modus_mode:
        vkoo_products_item_inherit.write({
            'active': True
        })
        vkoo_product_price_inherit.write({
            'active': True
        })
        vkoo_cart_lines_inherit.write({
            'active': True
        })
        vkoo_total_inherit.write({
            'active': True
        })
        vkoo_cart_summary_inherit.write({
            'active': True
        })
